mapper.select:
SELECT 	id, password
FROM   	members
WHERE	id = #{id} and password = #{password}

SELECT	id
FROM	members
WHERE	id = #{id} and pwquestion = #{pwquestion} and pwanswer = #{pwanswer}

mapper.update:
UPDATE	members
SET		password = #{password}
WHERE	id = #{id}

mapper.select:
SELECT	id
FROM	members
WHERE	id = #{id}

mapper.update:
UPDATE members
SET coincnt = coincnt + 1
WHERE id = #{id}
