mapper.select:
SELECT shid, id, shcontent, likecnt, commentcnt, shregdate, eximgfilename
FROM sharings
WHERE shid = #{shid}

mapper.update:
UPDATE sharings
SET shcontent = #{shcontent}
WHERE shid = #{shid}


	UPDATE sharings
	SET shcontent = #{shcontent}, eximgfilename = #{eximgfilename}
	WHERE shid = #{shid}

UPDATE sharings
SET likecnt = likecnt + 1
WHERE shid = #{shid}

UPDATE sharings
SET commentcnt = commentcnt + 1
WHERE shid = #{shid}

mapper.select:
SELECT shid, id
FROM likehistory
WHERE shid = #{shid}
