mapper.select:
SELECT recordfilename
FROM sharings
WHERE shid = #{shid}

SELECT shid, id, shcontent, likecnt, commentcnt, shregdate, eximgfilename
FROM sharings
WHERE shid = #{shid}

mapper.update:
UPDATE sharings
SET shcontent = #{shcontent}
WHERE shid = #{shid}


	UPDATE sharings
	SET shcontent = #{shcontent}, eximgfilename = #{eximgfilename}
	WHERE shid = #{shid}

UPDATE sharings
SET likecnt = likecnt + 1
WHERE shid = #{shid}

mapper.select:
SELECT likecnt
FROM sharings
WHERE shid = #{shid}

mapper.update:
UPDATE sharings
SET likecnt = likecnt - 1
WHERE shid = #{shid}

UPDATE sharings
SET commentcnt = commentcnt + 1
WHERE shid = #{shid}

mapper.select:
SELECT shid, id
FROM likehistory
WHERE shid = #{shid} and id = #{id}

SELECT rrid, id, recordfilename, recordregdate
FROM RecordRepository 
WHERE id = #{id}
ORDER BY recordregdate desc limit 5;
